mapper.select:
SELECT ui.id AS user_id, lt.lecture_id
FROM user_info ui
         JOIN lecture_ticket lt ON ui.id = lt.user_id
WHERE ui.cloud_id = #{openId}
  AND ui.deleted = 0 AND lt.deleted = 0;

SELECT COUNT(*) AS count
FROM lecture_ticket
WHERE user_id = #{userId}
  AND lecture_id = #{lectureId}
  AND deleted = 0;

SELECT is_lectured
FROM user_info
WHERE id = #{userId}
  AND deleted = 0;

mapper.update:
UPDATE user_info
SET is_lectured = 1,
    update_time = CURRENT_TIMESTAMP(0)
WHERE id = #{userId}
  AND deleted = 0
  AND is_lectured = 0;

mapper.delete:
UPDATE lecture_ticket
SET deleted     = 1,
    update_time = CURRENT_TIMESTAMP(0)
WHERE user_id = #{userId}
  AND lecture_id = #{lectureId}
  AND deleted = 0;

mapper.select:
SELECT ui.id AS user_id
FROM user_info ui
WHERE ui.cloud_id = #{openId}
  AND deleted = 0;

SELECT lecture_time
FROM lecture
WHERE lecture_id = #{lectureId}
AND deleted = 0;
